/**
 * Authorize user ad guest if not previously registered 
 *
 * Examples:
 *
 *     app.use(authorizeGuest());
 *
*/

/** 
* @return { Function }
* @api public
*/

module.exports = function authorizeGuest() {
  return function (req, res, next) {
    if (!req.user) {
      req.user = {username:'guest', password: 'guest', token: 'guest'}
    }
    next()
  }
}
